import { Component, OnInit } from '@angular/core';
import { SegUsuarioService } from 'src/app/services/seg_usuario.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  constructor(
    private _seg_usuario_Service: SegUsuarioService
  ){}
  ngOnInit(): void {
    this.getAllSegUsuario();
  }
  getAllSegUsuario(){
    this._seg_usuario_Service.getAllSegUsuario('ipp','seg_usuario').subscribe(data=>{
      console.log(data);
    })
  }
}
